import React, { useState } from "react";
import { Form, Button, Alert, Card } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const LoginPage = ({ onLogin }) => {
    const [loginForm, setLoginForm] = useState({ username: "", password: "" });
    const [loginError, setLoginError] = useState("");

    const handleLogin = (e) => {
        e.preventDefault();

        const { username, password } = loginForm;
        // Kiểm tra tài khoản và mật khẩu
        if (username === "thanhtam123" && password === "123456") {
            setLoginError("");
            onLogin(true); // Gọi hàm callback để xác nhận đăng nhập thành công
        } else {
            setLoginError("Tên đăng nhập hoặc mật khẩu không đúng!");
        }
    };

    return (
        <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
            <Card style={{ width: "400px" }} className="shadow">
                <Card.Body>
                    <h2 className="text-center mb-4">Đăng nhập Admin</h2>
                    {loginError && <Alert variant="danger">{loginError}</Alert>}
                    <Form onSubmit={handleLogin}>
                        <Form.Group className="mb-3">
                            <Form.Label>Tên đăng nhập</Form.Label>
                            <Form.Control
                                type="text"
                                value={loginForm.username}
                                onChange={(e) =>
                                    setLoginForm({ ...loginForm, username: e.target.value })
                                }
                                placeholder="Nhập tên đăng nhập"
                                className="p-2"
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Mật khẩu</Form.Label>
                            <Form.Control
                                type="password"
                                value={loginForm.password}
                                onChange={(e) =>
                                    setLoginForm({ ...loginForm, password: e.target.value })
                                }
                                placeholder="Nhập mật khẩu"
                                className="p-2"
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="w-100 p-2">
                            Đăng nhập
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </div>
    );
};

export default LoginPage;
