import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table, Button, Modal, Form } from "react-bootstrap";
import LoginPage from "./LoginPage"; // Import LoginPage
import "bootstrap/dist/css/bootstrap.min.css";

const AdminPage = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Trạng thái đăng nhập
    const [trips, setTrips] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [selectedTrip, setSelectedTrip] = useState(null);
    const [formData, setFormData] = useState({
        tripName: "",
        time: "",
        days: "",
        price: "",
        avatar: "",
        description: "",
    });

    const apiBaseUrl = "https://cloud-server-5ifq.onrender.com/api/trips";

    useEffect(() => {
        if (isLoggedIn) {
            const fetchTrips = async () => {
                try {
                    const response = await axios.get(apiBaseUrl);
                    setTrips(response.data);
                } catch (error) {
                    console.error("Lỗi khi lấy dữ liệu trips:", error);
                }
            };
            fetchTrips();
        }
    }, [isLoggedIn]);

    const handleShowModal = (trip = null) => {
        setSelectedTrip(trip);
        setFormData(
            trip
                ? { ...trip }
                : {
                      tripName: "",
                      time: "",
                      days: "",
                      price: "",
                      avatar: "",
                      description: "",
                  }
        );
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setSelectedTrip(null);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (
            !formData.tripName ||
            !formData.time ||
            !formData.days ||
            !formData.price ||
            !formData.avatar ||
            !formData.description
        ) {
            alert("Vui lòng điền đầy đủ thông tin!");
            return;
        }

        try {
            if (selectedTrip) {
                const response = await axios.put(`${apiBaseUrl}/${selectedTrip._id}`, formData);
                if (response.status === 200) {
                    setTrips((prev) =>
                        prev.map((trip) =>
                            trip._id === selectedTrip._id ? { ...trip, ...response.data } : trip
                        )
                    );
                    handleCloseModal();
                }
            } else {
                const response = await axios.post(apiBaseUrl, formData);
                if (response.status === 201) {
                    setTrips((prev) => [...prev, response.data]);
                    handleCloseModal();
                }
            }
        } catch (error) {
            console.error("Lỗi khi gửi dữ liệu:", error);
        }
    };

    const handleDelete = async (tripId) => {
        try {
            await axios.delete(`${apiBaseUrl}/${tripId}`);
            setTrips((prev) => prev.filter((trip) => trip._id !== tripId));
        } catch (error) {
            console.error("Lỗi khi xóa trip:", error);
        }
    };

    return (
        <div className="container mt-5">
            {!isLoggedIn ? (
                <LoginPage onLogin={setIsLoggedIn} />
            ) : (
                <>
                    <h1 className="text-center mb-4">Quản lý Trips</h1>
                    <Button variant="primary" onClick={() => handleShowModal()} className="mb-3">
                        Thêm Trip
                    </Button>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Trip Name</th>
                                <th>Time</th>
                                <th>Days</th>
                                <th>Price</th>
                                <th>Avatar</th>
                                <th>Description</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {trips.map((trip, index) => (
                                <tr key={trip._id}>
                                    <td>{index + 1}</td>
                                    <td>{trip.tripName}</td>
                                    <td>{new Date(trip.time).toLocaleString()}</td>
                                    <td>{trip.days}</td>
                                    <td>{trip.price}</td>
                                    <td>
                                        <img
                                            src={trip.avatar}
                                            alt={trip.tripName}
                                            style={{ width: "100px" }}
                                        />
                                    </td>
                                    <td>{trip.description}</td>
                                    <td>
                                        <Button
                                            variant="warning"
                                            className="me-2"
                                            onClick={() => handleShowModal(trip)}
                                        >
                                            Sửa
                                        </Button>
                                        <Button
                                            variant="danger"
                                            onClick={() => handleDelete(trip._id)}
                                        >
                                            Xóa
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </>
            )}
        </div>
    );
};

export default AdminPage;
