import React, { useState } from "react";
import { Form, Button, Alert, Card } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const LoginPage = ({ onLogin }) => {
    const [loginForm, setLoginForm] = useState({ username: "", password: "" });
    const [loginError, setLoginError] = useState("");

    const handleLogin = (e) => {
        e.preventDefault();

        const { username, password } = loginForm;
        // Kiểm tra tài khoản và mật khẩu
        if (username === "thanhtam123" && password === "123456") {
            setLoginError("");
            onLogin(true); // Gọi hàm callback để xác nhận đăng nhập thành công
        } else {
            setLoginError("Tên đăng nhập hoặc mật khẩu không đúng!");
        }
    };

    return (
        <section>
            <div className="container mt-5 p-5">
                <div className="row">
                    {/* Cột chứa hình ảnh */}
                    <div className="col-md-6 p-5">
                        <img
                            alt="Banner"
                            src="/img/banner-img4.png" // Đảm bảo đường dẫn đến ảnh chính xác
                            width="100%"
                            height="100%"
                        />
                    </div>

                    {/* Cột chứa form đăng nhập */}
                    <div className="col-md-6 mt-3 p-5">
                        <Card className="shadow p-3 mb-5 bg-body-tertiary rounded">
                            <div className="card-header">
                                <p className="fs-4 text-center">Login</p>
                                {loginError && (
                                    <Alert variant="danger" className="text-center">
                                        {loginError}
                                    </Alert>
                                )}
                            </div>
                            <div className="card-body">
                                <Form onSubmit={handleLogin}>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Email</Form.Label>
                                        <Form.Control
                                            required
                                            type="email"
                                            name="username"
                                            value={loginForm.username}
                                            onChange={(e) =>
                                                setLoginForm({
                                                    ...loginForm,
                                                    username: e.target.value,
                                                })
                                            }
                                            placeholder="Nhập email"
                                            className="form-control"
                                        />
                                    </Form.Group>

                                    <Form.Group className="mb-3">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control
                                            required
                                            type="password"
                                            name="password"
                                            value={loginForm.password}
                                            onChange={(e) =>
                                                setLoginForm({
                                                    ...loginForm,
                                                    password: e.target.value,
                                                })
                                            }
                                            placeholder="Nhập mật khẩu"
                                            className="form-control"
                                        />
                                    </Form.Group>

                                    <Button
                                        type="submit"
                                        className="btn bg-primary text-white col-md-12"
                                    >
                                        Login
                                    </Button>
                                </Form>
                            </div>
                            <div className="card-footer text-center">
                                Don't have an account?{" "}
                                <a href="/register" className="text-decoration-none">
                                    Create one
                                </a>
                            </div>
                        </Card>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default LoginPage;
